document.addEventListener("DOMContentLoaded",(function(){const container=document.getElementById("paneContainer");let activeResizer=null;let initialX=0;let initialWidth=0;let paneToResize=null;let totalWidth=container.offsetWidth;const resizers=document.querySelectorAll(".pane-resizer");resizers.forEach((resizer=>{resizer.addEventListener("mousedown",startResize)}));function startResize(e){e.preventDefault();activeResizer=e.target;activeResizer.classList.add("active");const paneId=activeResizer.dataset.pane;paneToResize=document.getElementById(paneId);initialX=e.clientX;initialWidth=paneToResize.offsetWidth;document.addEventListener("mousemove",resize);document.addEventListener("mouseup",stopResize)}function resize(e){if(!activeResizer)return;const dx=e.clientX-initialX;const newWidth=initialWidth+dx;if(newWidth>=100){paneToResize.style.width=newWidth+"px";paneToResize.style.minWidth=newWidth+"px";paneToResize.style.flexGrow=0;paneToResize.style.flexShrink=0;if(paneToResize.id==="pane2"){const lastPane=document.getElementById("pane3");const pane1Width=document.getElementById("pane1").offsetWidth;const remainingWidth=totalWidth-pane1Width-newWidth;if(remainingWidth>=100){lastPane.style.width=`${remainingWidth}px`;lastPane.style.flexGrow=1}}if(paneToResize.id==="pane1"){const lastPane=document.getElementById("pane3");const pane2Width=document.getElementById("pane2").offsetWidth;const remainingWidth=totalWidth-newWidth-pane2Width;if(remainingWidth>=100){lastPane.style.width=`${remainingWidth}px`;lastPane.style.flexGrow=1}}}}function stopResize(){if(activeResizer){activeResizer.classList.remove("active")}activeResizer=null;document.removeEventListener("mousemove",resize);document.removeEventListener("mouseup",stopResize);document.body.style.cursor="default"}resizers.forEach((resizer=>{resizer.addEventListener("dblclick",(function(e){const paneId=e.target.dataset.pane;const pane=document.getElementById(paneId);if(pane.dataset.prevWidth){pane.style.width=pane.dataset.prevWidth;pane.style.minWidth=pane.dataset.prevWidth;pane.dataset.prevWidth=""}else{pane.dataset.prevWidth=pane.style.width;pane.style.width="50px";pane.style.minWidth="50px"}const pane1Width=document.getElementById("pane1").offsetWidth;const pane2Width=document.getElementById("pane2").offsetWidth;const lastPane=document.getElementById("pane3");const remainingWidth=totalWidth-pane1Width-pane2Width;if(remainingWidth>=100){lastPane.style.width=`${remainingWidth}px`;lastPane.style.flexGrow=1}}))}));window.addEventListener("resize",(function(){totalWidth=container.offsetWidth;const pane1Width=document.getElementById("pane1").offsetWidth;const pane2Width=document.getElementById("pane2").offsetWidth;const lastPane=document.getElementById("pane3");const remainingWidth=totalWidth-pane1Width-pane2Width;if(remainingWidth>=100){lastPane.style.width=`${remainingWidth}px`;lastPane.style.flexGrow=1}}));document.getElementById("pane1").style.flexGrow=0;document.getElementById("pane1").style.flexShrink=0;document.getElementById("pane2").style.flexGrow=0;document.getElementById("pane2").style.flexShrink=0;document.getElementById("pane3").style.flexGrow=1}));